(function() {
  var app;

  app = angular.module('opendyslexic', []);

  app.controller('core', function($scope) {
    $scope.enabled = false;
    $scope.message = $scope.enabled ? "On" : "Off";
    $scope.init = function() {
      chrome.storage.sync.get({
        booleans: false
      }, function(items) {
        console.log(items);
        if (items.booleans) {
          $scope.enabled = 1;
        } else {
          $scope.enabled = 0;
        }
      });
    };

    /**
     * Adds Saves the Optoins
     */
    $scope.openDyslexic = function() {
      chrome.storage.sync.set({
        booleans: $scope.enabled
      }, function() {
        return $scope.reloadPage();
      });
      return;
    };
    return $scope.reloadPage = function() {
      chrome.tabs.getSelected(null, function(tab) {
        var code;
        code = 'window.location.reload();';
        chrome.tabs.executeScript(tab.id, {
          code: code
        });
      });
    };
  });

  return;

}).call(this);
